SELECT RobberId, Nickname FROM ROBBERS NATURAL JOIN(SELECT RobberId FROM ACCOMPLICES GROUP BY RobberId HAVING COUNT(RobberId)>((SELECT COUNT(RobberId) FROM ACCOMPLICES)/(SELECT COUNT(DISTINCT RobberId) FROM ACCOMPLICES))) AS Actives NATURAL JOIN (SELECT RobberId, SUM(Share) AS RobberEarnings FROM ACCOMPLICES GROUP BY RobberId) AS Earnings WHERE NoYears = 0 ORDER BY RobberEarnings DESC;